	/* This is the sample RedirectURL PHP Page. It can be directly used for integration with CCAvenue if your application is 
	   developed in PHP. You need to simply change the variables to match your variables as well as insert routines for handling a 	   successful or unsuccessful transaction. return values i.e the parameters below are passed as POST parameters by CCAvenue   	
	   server 
	*/

	error_reporting(0);
	$workingKey='avmfwscu865nulupnjbnjxi3z4t8ylc1';		//Working Key should be provided here.
	$encResponse=$_POST["encResponse"];			//This is the response sent by the CCAvenue Server
	$rcvdString=decrypt($encResponse,$workingKey);		//AES Decryption used as per the specified working key.
	$AuthDesc="";
	$MerchantId="";
	$OrderId="";
	$Amount=0;
	$Checksum=0;
	$veriChecksum=false;
	global $decryptValues;
	global $dataSize;
	$decryptValues=explode('&', $rcvdString);
	$dataSize=sizeof($decryptValues);
	//******************************    Messages based on Checksum & AuthDesc   **********************************//
	
	for($i = 0; $i < $dataSize; $i++) 
	{
		$information=explode('=',$decryptValues[$i]);
		if($i==0)	$MerchantId=$information[1];	
		if($i==1)	$OrderId=$information[1];
		if($i==2)	$Amount=$information[1];	
		if($i==3)	$AuthDesc=$information[1];
		if($i==4)	$Checksum=$information[1];	
	}

	$rcvdString=$MerchantId.'|'.$OrderId.'|'.$Amount.'|'.$AuthDesc.'|'.$workingKey;
	$veriChecksum=verifyChecksum(genchecksum($rcvdString), $Checksum);

		//Load connection Class
		function __autoload($class_name)
		{
			require $class_name.'.php';
			}
		//call connect_db function to make db connection.
		$connectObj = new Connection();
		$connectObj->connect_db();

	
	//Change role of user
	function changeRole($roleId, $uid, $data)
	{
		
		if($data['bankRespCode'] == 0)
		{
			$query = "select rid from users_roles where uid=".$uid." ";
			$queryRes = mysql_query($query);
			if(mysql_num_rows($queryRes))
			{
				$result = mysql_fetch_array($queryRes);
				$currentRoleId = $result['rid'];
				if($roleId == $currentRoleId && $currentRoleId != 20)
				{
					$query = "update users_roles set rid=".$roleId." where uid=".$uid." ";
					mysql_query($query);
					}
				else
				{
					
					$query = "insert into users_roles(uid, rid) values(".$uid.",".$roleId.")";
					mysql_query($query);
					}
				}
			else
			{
				$query = "insert into users_roles(uid, rid) values(".$uid.",".$roleId.")";
				mysql_query($query);
				}
			$emailBody = "Your service will be activate with in 24 hours";
		}
		else 
